package com.leetcode.questions;

import org.junit.Before;
import org.junit.Test;

import static org.assertj.core.api.Assertions.assertThat;

public class GreatestCommonDivisorOfStringsTest {
    private GreatestCommonDivisorOfStrings greatestCommonDivisorOfStrings;

    @Before
    public void setUp() {
        greatestCommonDivisorOfStrings = new GreatestCommonDivisorOfStrings();
    }

    @Test
    public void test_leetcode_1() {
        String str1 = "ABCABC", str2 = "ABC";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("ABC");
    }

    @Test
    public void test_leetcode_2() {
        String str1 = "ABABAB", str2 = "ABAB";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("AB");
    }

    @Test
    public void test_leetcode_3() {
        String str1 = "LEET", str2 = "CODE";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("");
    }

    @Test
    public void test_leetcode_57() {
        String str1 = "";
        String str2 = "";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo(str1);
    }

    @Test
    public void test_leetcode_72() {
        String str1 = "";
        String str2 = "";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");
    }

    @Test
    public void test_withSingleCharacterInBothStrings() {
        String str1 = "AAAAAA", str2 = "AAAAA";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("A");
    }

    @Test
    public void test_withOneCharacterLongStrings() {
        String str1 = "A", str2 = "A";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo("A");
    }

    @Test
    public void test_withSingleCharacterAndEqualLengthStrings() {
        String str1 = "AAAAA", str2 = "AAAAA";

        assertThat(greatestCommonDivisorOfStrings.gcdOfStrings(str1, str2)).isEqualTo(str1);
    }
}